package java_package;

public class IfelseClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=5;
		if(a>b)
		{
			
		System.out.println("The greater number is "+a);
		}
		else
		{
			System.out.println("The smaller number is "+b);
		}
		}


}

